package methods;

public class Case10 {
	
	//Scenario Java doesn't support operator overloading concept. but one implicit overloaded operator is + .

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("ratan"+"durga");
		System.out.println(10+20);
		System.out.println("ratan"+10);
		System.out.println("ratan"+10+"durga"+10);
		int a=10,b=20,c=30;
		//print all in one line
		System.out.println(a+" "+b+" "+c);

	}

}
